function Main() {

    function handleEditAvatarClick () {
        const profileEditButton = document.querySelector('.profile__edit-button');
        const profileEditForm = new PopupWithForm({
            popupSelector: ".page__dialog_type_edit-profile",
            handleFormSubmit: (data) => {
                loading(true, ".page__dialog_type_edit-profile");
                api.setUserInfo(data)
                    .then(res => {
                        profile.setUserInfo({ name: res.name, job: res.about, avatarImg: res.avatar })
                        profileEditForm.close();
                        loading(false, ".page__dialog_type_edit-profile");
                    })
                    .catch(err => console.log)
            }
        });

        profileEditButton.addEventListener('click', () => {
            const user = profile.getUserInfo();
            profileNameInput.value = user.name;
            profileJobInput.value = user.job;
            profileEditForm.open();
        });

        profileEditForm.setEventListeners();
    }

    function handleEditProfileClick () {

    }

    function handleAddPlaceClick () {

    }


    return (
        <main className="content">
            <section className="profile">
                <div className="profile__left-container">
                    <div className="profile__avatar-container">
                        <img src="#" alt="Profile Avatar" className="profile__avatar"/>
                        <button type="button" name="update avatar"
                                className="profile__avatar_edit"/>
                    </div>
                    <div className="profile__info">
                        <h1 className="profile__name"/>
                        <button className="profile__edit-button" type="button" name="edit profile"/>
                        <p className="profile__job"/>
                    </div>
                </div>
                <button className="profile__add-button" type="button" name="add card"/>
            </section>

            <section className="elements">
                <ul className="elements__grid">
                </ul>
            </section>


        </main>
    );
}

export default Main;
