import React, {useEffect} from "react";
import Header from './Header';
import Main from './Main';
import Footer from './Footer';
import ImagePopup from "./ImagePopup";
import PopupWithForm from "./PopupWithForm";
import Input from "./Input";

function App() {

    const [selectedCard, setSelectedCard] = React.useState(null);
    const [profileDialog, setProfileDialog] = React.useState(false);
    const [avatarDialog, setAvatarDialog] = React.useState(false);
    const [cardDialog, setCardDialog] = React.useState(false);
    const [deleteDialog, setDeleteDialog] = React.useState(false);

    function handleCardClick(card) {
        setSelectedCard(card);
    }

    function handleEditAvatarClick() {
        setAvatarDialog(true);
    }

    function handleEditProfileClick() {
        setProfileDialog(true);
    }

    function handleAddPlaceClick() {
        setCardDialog(true);
    }

    function handleCardDelete() {
        setDeleteDialog(true);
    }

    function closeAllPopups() {
        setSelectedCard(false);
        setProfileDialog(false);
        setAvatarDialog(false);
        setCardDialog(false);
        setDeleteDialog(false);
    }

    return (
        <div className="page">
            <div className="page__content">
                <Header/>
                <Main
                    onCardClick={handleCardClick}
                    onProfileEdit={handleEditProfileClick}
                    onAvatarEdit={handleEditAvatarClick}
                    onCardAdd={handleAddPlaceClick}
                    onCardDelete={handleCardDelete}
                />
                <Footer/>
            </div>

            <PopupWithForm
                name='edit-profile'
                //type = 'update-profile'
                title='Edit profile'
                isOpen={profileDialog}
                onClose={closeAllPopups}
                submitText='Save'>
                    <Input

                    />
            </PopupWithForm>

            <PopupWithForm
                name='edit-avatar'
                // type = ''
                title='Change profile picture'
                isOpen={avatarDialog}
                onClose={closeAllPopups}
                submitText='Save'
            />

            <PopupWithForm
                name='add-grid-item'
                // type = ''
                title='New place'
                isOpen={cardDialog}
                onClose={closeAllPopups}
                submitText='Save'
            />

            <PopupWithForm
                name='delete-confirmation'
                // type = ''
                title='Are you sure?'
                isOpen={deleteDialog}
                onClose={closeAllPopups}
                submitText='Yes'
            />


            <template className="grid-template">

            </template>
            <ImagePopup
                selectedCard={selectedCard}
                onClose={closeAllPopups}
            />

        </div>
    );
}

export default App;
