import React, {useEffect} from "react";
import Header from './Header';
import Main from './Main';
import Footer from './Footer';
import ImagePopup from "./ImagePopup";
import PopupWithForm from "./PopupWithForm";

function App() {

    const [selectedCard, setSelectedCard] = React.useState(null);

    function handleCardClick(card) {
        setSelectedCard(card);
    }

    function closeAllPopups() {
        setSelectedCard(false);
    }

    return (
        <div className="page">
            <div className="page__content">
                <Header/>
                <Main
                    onCardClick = {handleCardClick}
                />
                <Footer/>
            </div>

            <PopupWithForm
                name = 'edit-profile'
               // type = 'update-profile'
                title = 'Edit profile'
                isOpen = {profileDialog}
                onClose= {closeAllPopups}
                submitText= 'Save'
            />

            <PopupWithForm
                name = 'edit-avatar'
               // type = ''
                title = 'Change profile picture'
                isOpen = {avatarDialog}
                onClose= {closeAllPopups}
                submitText= 'Save'
            />

            <PopupWithForm
                name = 'add-grid-item'
                // type = ''
                title = 'New place'
                isOpen = {cardDialog}
                onClose= {closeAllPopups}
                submitText= 'Save'
            />

            <PopupWithForm
                name = 'delete-confirmation'
                // type = ''
                title = 'Are you sure?'
                isOpen = {cardDialog}
                onClose= {closeAllPopups}
                submitText= 'Save'
            />


            <div className="page__dialog_type_delete-confirmation dialog">
                <div className="dialog__container">
                    <button className="dialog__close-button" type="button"/>

                    <form action="#" className="dialog__form" method="post" name="delete-confirmation">
                        <h3 className="dialog__header">
                            Are you sure?
                        </h3>
                        <button type="submit" className="dialog__button">Yes</button>
                    </form>
                </div>
            </div>


            <template className="grid-template">

            </template>
            <ImagePopup
                selectedCard={selectedCard}
                onClose={closeAllPopups}
            />

        </div>
    );
}

export default App;
